mapper.select:
select s1.*,s2.code from (select id,username,password,avatar,name,birthday,sex,phone,salt,status,organ_id from sys_user where username!='admin'
and username like concat('%',#{username},'%')
and name like concat('%',#{name},'%')
and status = #{status}
)s1 left JOIN (select id,code from sys_organ)s2 on s1.organ_id = s2.id where 1=1
and code like concat(#{organCode},'%')
